package Practice;

import java.util.*;

public class Que_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);

		System.out.println("Enter Your Age");
		int Age = sc.nextInt();

		//increment age as 01 by 01.
		
		Age = Age + 1;
		
		System.out.println("Your age is:"+Age);
		sc.close();
	}

}
